mapper.select:
SELECT com01_corp_cd as com01CorpCd, com01_corp_nm as com01CorpNm, com01_corp_type as com01CorpType, com01_corp_enm as com01CorpEnm, com01_extn_corp_cd as com01ExtnCorpCd, com01_corp_type_nm as com01CorpTypeNm FROM ( SELECT com01_corp.* , f_com_nm('CorpType', com01_corp_type) as com01_corp_type_nm FROM com01_corp ) t WHERE 1=1
ORDER BY com01_corp_type, com01_corp_cd, com01_corp_nm
OFFSET ${pageAttr.offset}
LIMIT ${pageAttr.limit}
